var raw = msg.payload.characteristics["0003cdd100001000800000805f9b0131"].toString();

if(raw.length > 1) {
    msg.message = raw.substr(0, 1);

    var n = raw.indexOf("Z");
    var trimmed = raw;
    if(n != -1) {
        trimmed = raw.substring(1, n - 1);
    }
    else {
        trimmed = raw.substring(1, raw.length - 1);
    }

    msg.parameters = trimmed.split(",");
}

msg.payload = raw;

return msg;

var targetTemp = msg.parameters[0];
var currentTemp = msg.parameters[1];

// Grainfather set target temp to 120 °C during boil
if(targetTemp > 100.0) {
    targetTemp = 100.0;
}
node.send([{'payload':targetTemp},null, null]);
node.send([null,{'payload':currentTemp}, null]);

var payload = {};
temperature_unit = flow.get('temperature_unit');
if(temperature_unit === 'F') {
    targetTemp = (targetTemp * 9 / 5) + 32;
    currentTemp = (currentTemp * 9 / 5) + 32;
}

payload.temperature_unit = temperature_unit;
payload.targetTemp = Number(targetTemp).toFixed(0);
payload.currentTemp = Number(currentTemp).toFixed(1);

msg.payload = payload;
node.send([null, null, msg]);
//return msg;

function zeropad(num) {
    var s = "0" + num;
    return s.substr(s.length - 2);
}

var timerActive = Boolean(msg.parameters[0] === "1");
var timerLeftMins = 0;
var timerStartMins = 0;
var timerLeftSecs = 0;
var timerFormatted = "--";

if(timerActive) {
    timerLeftSecs = Number(msg.parameters[3]);
    timerLeftMins = Number(msg.parameters[1]) - 1 + Math.floor(timerLeftSecs / 60);
    timerStartMins = Number(msg.parameters[2]);
    timerLeftSecs = timerLeftSecs % 60;
    if(timerLeftMins <= 59) {
        timerFormatted = timerLeftMins.toString() + ":" + zeropad(timerLeftSecs);
    }
    else {
        var hours = Math.floor(timerLeftMins / 60);
        var mins = timerLeftMins % 60;
        timerFormatted = hours.toString() + ":" + zeropad(mins) + ":" + zeropad(timerLeftSecs);
        //timerFormatted = "_:__:__";
    }
}

node.send([{'payload':timerActive}, null, null, null, null]);
node.send([null, {'payload':timerStartMins}], null, null, null);
node.send([null, null, {'payload':timerLeftMins}], null, null);
node.send([null, null, null, {'payload':timerLeftSecs}], null);
node.send([null, null, null, null, {'payload':timerFormatted}]);

//return msg;

